import React, { useState, useEffect } from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Video.css';

const VideoCarousel = () => {
  const [isPlaying, setIsPlaying] = useState(false);

  const videos = [
    { src: 'https://www.youtube.com/embed/yGmkh79rxDA', title: 'Video 1', description: 'This is the description for video 1.' },
    { src: 'https://www.youtube.com/embed/yGmkh79rxDA', title: 'Video 2', description: 'This is the description for video 2.' },
    { src: 'https://www.youtube.com/embed/yGmkh79rxDA', title: 'Video 3', description: 'This is the description for video 3.' },
    { src: 'https://www.youtube.com/embed/yGmkh79rxDA', title: 'Video 4', description: 'This is the description for video 4.' },
    { src: 'https://www.youtube.com/embed/yGmkh79rxDA', title: 'Video 5', description: 'This is the description for video 5.' }
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: !isPlaying, // Pause autoplay when a video is playing
    autoplaySpeed: 3000,
    pauseOnHover: false, // Don't pause when hovering over the slider
  };

  const handleVideoPlay = () => {
    setIsPlaying(true); // Pause autoplay when video is clicked
  };

  const handleVideoPause = () => {
    setIsPlaying(false); // Resume autoplay when video is paused
  };

  return (
    <Slider {...settings}>
      {videos.map((video, index) => (
        <div key={index} className="carousel" style={{ display: 'flex', flexDirection: 'row', alignItems: 'center' }}>
          <div className="description" style={{ marginRight: '20px' }}>
            <h3>{video.title}</h3>
            <p>{video.description}</p>
          </div>
          <div className="video-container">
            <iframe
              width="400"
              height="250"
              src={video.src}
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
              title={video.title}
              onPlay={handleVideoPlay} // Trigger when video plays
              onPause={handleVideoPause} // Trigger when video pauses
            />
          </div>
        </div>
      ))}
    </Slider>
  );
};

export default VideoCarousel;
