import React, { useRef } from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Video.css';

const VideoCarousel = () => {
  const videoRefs = useRef([]);

  const videos = [
    { src: 'https://www.w3schools.com/html/mov_bbb.mp4', title: 'Video 1', description: 'This is the description for video 1.' },
    { src: 'https://www.w3schools.com/html/mov_bbb.mp4', title: 'Video 2', description: 'This is the description for video 2.' },
    { src: 'https://www.w3schools.com/html/mov_bbb.mp4', title: 'Video 3', description: 'This is the description for video 3.' },
    { src: 'https://www.w3schools.com/html/mov_bbb.mp4', title: 'Video 4', description: 'This is the description for video 4.' },
    { src: 'https://www.w3schools.com/html/mov_bbb.mp4', title: 'Video 5', description: 'This is the description for video 5.' }
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    beforeChange: (current, next) => {
      if (videoRefs.current[current]) {
        videoRefs.current[current].pause(); // Pause the outgoing video
        videoRefs.current[current].currentTime = 0; // Reset the outgoing video to the start
      }
    },
    afterChange: (current) => {
      if (videoRefs.current[current]) {
        videoRefs.current[current].play(); // Automatically play the incoming video
      }
    },
  };

  return (
    <Slider {...settings}>
      {videos.map((video, index) => (
        <div key={index} className="carousel" style={{ display: 'flex', flexDirection: 'row', alignItems: 'center' }}>
          <div className="description" style={{ marginRight: '20px' }}>
            <h3>{video.title}</h3>
            <p>{video.description}</p>
          </div>
          <div className="video-container">
            <video
              width="400"
              ref={(el) => (videoRefs.current[index] = el)} // Store a reference to each video element
              muted // Ensure autoplay works on most browsers by muting the video
            >
              <source src={video.src} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        </div>
      ))}
    </Slider>
  );
};

export default VideoCarousel;
