import React, { useState, useEffect } from 'react';
import './Video.css'
const VideoCarousel = () => {
  const videos = [
    {
      src: 'video1.mp4', 
      title: 'Video 1',
      description: 'This is the description for video 1.'
    },
    {
      src: 'video2.mp4',
      title: 'Video 2',
      description: 'This is the description for video 2.'
    },
    {
      src: 'video3.mp4', 
      title: 'Video 3',
      description: 'This is the description for video 3.'
    },
    {
      src: 'video4.mp4', 
      title: 'Video 4',
      description: 'This is the description for video 4.'
    },
    {
      src: 'video5.mp4', 
      title: 'Video 5',
      description: 'This is the description for video 5.'
    }
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false); // State to manage the transition effect

  useEffect(() => {
    const intervalId = setInterval(() => {
      setIsTransitioning(true); // Start the transition
      setTimeout(() => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % videos.length);
        setIsTransitioning(false); // End the transition after changing the index
      }, 500); // Match this duration with the CSS transition duration
    }, 3000); // Change video every 3 seconds

    return () => clearInterval(intervalId); // Clear interval on component unmount
  }, [videos.length]);

  return (
    <div className="video-carousel">
      <div className={`video-description-container ${isTransitioning ? 'transitioning' : ''}`}>
        <div className="video-container">
          <video controls width="150">
            <source src={videos[currentIndex].src} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
        <div className="description">
          <h3>{videos[currentIndex].title}</h3>
          <p>{videos[currentIndex].description}</p>
        </div>
      </div>
    </div>
  );
};

export default VideoCarousel;
