import React, { useState, useEffect } from 'react';

const VideoCarousel = () => {
  const videos = [
    { src: 'video1.mp4', title: 'Video 1', description: 'This is the description for video 1.' },
    { src: 'video2.mp4', title: 'Video 2', description: 'This is the description for video 2.' },
    { src: 'video3.mp4', title: 'Video 3', description: 'This is the description for video 3.' },
    { src: 'video4.mp4', title: 'Video 4', description: 'This is the description for video 4.' },
    { src: 'video5.mp4', title: 'Video 5', description: 'This is the description for video 5.' }
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % videos.length);
    }, 3000);
    return () => clearInterval(intervalId);
  }, [videos.length]);

  return (
    <div className="video-carousel">
      <div className="video-description-container">
        <video controls width="400">
          <source src={videos[currentIndex].src} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        <div className="description">
          <h3>{videos[currentIndex].title}</h3>
          <p>{videos[currentIndex].description}</p>
        </div>
      </div>
    </div>
  );
};

export default VideoCarousel;
