import React, { useState } from "react";
import "./Coupon.css"; // Import the CSS file

const CouponInput = () => {
  const [couponCode, setCouponCode] = useState("");
  const [isCouponValid, setIsCouponValid] = useState(false);

  // Example list of valid coupons
  const validCoupons = ["DISCOUNT10", "FREESHIP", "WELCOME20"];

  const handleInputChange = (e) => {
    const code = e.target.value.toUpperCase();
    setCouponCode(code);
    setIsCouponValid(validCoupons.includes(code));
  };

  const applyCoupon = () => {
    if (isCouponValid) {
      alert(`Coupon "${couponCode}" applied successfully!`);
    }
  };

  return (
    <div className="coupon-container">
      <input
        type="text"
        value={couponCode}
        onChange={handleInputChange}
        placeholder="Enter coupon code"
        className="coupon-input"
      />
      <button
        onClick={applyCoupon}
        disabled={!isCouponValid}
        className={`apply-button ${isCouponValid ? "active" : ""}`}
      >
        Apply
      </button>
    </div>
  );
};

export default CouponInput;
