import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Video.css';

const VideoCarousel = () => {
  const videos = [
    { src: 'https://www.youtube.com/watch?v=yGmkh79rxDA&list=RDyGmkh79rxDA&start_radio=1', title: 'Video 1', description: 'This is the description for video 1.' },
    { src: 'https://www.youtube.com/watch?v=yGmkh79rxDA&list=RDyGmkh79rxDA&start_radio=1', title: 'Video 2', description: 'This is the description for video 2.' },
    { src: 'video3.mp4', title: 'Video 3', description: 'This is the description for video 3.' },
    { src: 'video4.mp4', title: 'Video 4', description: 'This is the description for video 4.' },
    { src: 'video5.mp4', title: 'Video 5', description: 'This is the description for video 5.' }
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <Slider {...settings}>
      {videos.map((video, index) => (
        <div key={index} className="carousel" style={{ display: 'flex', flexDirection: 'row' }}>
          <div className="description">
            <h3>{video.title}</h3>
            <p>{video.description}</p>
          </div>
          <div className="video-container">
            <video controls width="150">
              <source src={video.src} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        </div>
      ))}
    </Slider>
  );
};

export default VideoCarousel;
