import "./Test.css";
import React, { useState, useRef,useEffect } from "react";
import { Carousel } from "react-bootstrap";
import ReactPlayer from "react-player";
import "bootstrap/dist/css/bootstrap.css";
import Imgv from '../assets/video 1.png'

const VideoCarousel = () => {



  const videoProperties = [
    {
      id: 1,
      title: "Video 1",
      src: 'https://www.youtube.com/watch?v=ZaWQql80sg8',
      credit: "Video by cottonbro from Pexels",
    },
    {
      id: 2,
      title: "Video 2",
      src: 'https://www.youtube.com/watch?v=ZaWQql80sg8',
      credit: "Video by cottonbro from Pexels",
    },
    {
      id: 3,
      title: "Video 3",
      src: 'https://www.youtube.com/watch?v=ZaWQql80sg8',
      credit: "Video by cottonbro from Pexels",
    },
  ];

  // State to store the playback positions for each video
  const [playbackPositions, setPlaybackPositions] = useState(
    videoProperties.reduce((acc, video) => {
      acc[video.id] = 0; // Initialize each video's position to 0
      return acc;
    }, {})
  );

  // Update the playback position of a video as it progresses
  const handleProgress = (videoId, playedSeconds) => {
    setPlaybackPositions((prevPositions) => ({
      ...prevPositions,
      [videoId]: playedSeconds,
    }));
  };
  const itemsRef = useRef([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  // Function to scroll to a specific item by index
  const scrollToItem = (index) => {
    if (itemsRef.current[index]) {
      window.scrollTo({
        top: itemsRef.current[index].offsetTop,
        behavior: 'smooth'
      });
    }
  };

  // Handle click to scroll to the next item
  const handleNextScroll = () => {
    const nextIndex = (currentIndex + 1) % itemsRef.current.length;
    setCurrentIndex(nextIndex);
    scrollToItem(nextIndex);
  };

  useEffect(()=>{
    handleNextScroll()
  },[])


  return (

        <div className="Vid">
            <div className="Vids" ref={(el) => (itemsRef.current[idx] = el)}>
