import "./Test.css";
import React, { useState } from "react";
import { Carousel } from "react-bootstrap";
import ReactPlayer from "react-player";
import "bootstrap/dist/css/bootstrap.css";

const VideoCarousel = () => {
  const videoProperties = [
    {
      id: 1,
      title: "Video 1",
      src: 'https://www.youtube.com/watch?v=ZaWQql80sg8',
      credit: "Video by cottonbro from Pexels",
    },
    {
      id: 2,
      title: "Video 2",
      src: 'https://www.youtube.com/watch?v=ZaWQql80sg8',
      credit: "Video by cottonbro from Pexels",
    },
    {
      id: 3,
      title: "Video 3",
      src: 'https://www.youtube.com/watch?v=ZaWQql80sg8',
      credit: "Video by cottonbro from Pexels",
    },
  ];

  // State to store the playback positions for each video
  const [playbackPositions, setPlaybackPositions] = useState(
    videoProperties.reduce((acc, video) => {
      acc[video.id] = 0; // Initialize each video's position to 0
      return acc;
    }, {})
  );

  // Update the playback position of a video as it progresses
  const handleProgress = (videoId, playedSeconds) => {
    setPlaybackPositions((prevPositions) => ({
      ...prevPositions,
      [videoId]: playedSeconds,
    }));
  };

  return (
    <div className="App">
      <Carousel>
        {videoProperties.map((videoObj) => {
          return (
            <Carousel.Item key={videoObj.id}>
              <ReactPlayer
                url={videoObj.src}
                playing={true}      // Autoplay enabled
                controls={false}    // Controls removed
                muted={true}        // Video is muted for autoplay
                loop={true}         // Loop enabled
                width="350px"        // Adjust width if necessary
                height="34px"      // Adjust height if necessary
                // Start the video at the last saved position
                played={playbackPositions[videoObj.id]}
                onProgress={({ playedSeconds }) => handleProgress(videoObj.id, playedSeconds)}
              />
              <Carousel.Caption>
                <h3>{videoObj.title}</h3>
                <p>{videoObj.credit}</p>
              </Carousel.Caption>
            </Carousel.Item>
          );
        })}
      </Carousel>
    </div>
  );
};

export default VideoCarousel;
