import React, { useState } from "react";

function App() {
  // Step 1: State to manage registration and login details
  const [registerData, setRegisterData] = useState({ username: "", password: "" });
  const [loginData, setLoginData] = useState({ username: "", password: "" });
  const [loggedInUser, setLoggedInUser] = useState(null);

  // Step 2: Handle registration input changes
  const handleRegisterChange = (e) => {
    setRegisterData({ ...registerData, [e.target.name]: e.target.value });
  };

  // Step 3: Handle login input changes
  const handleLoginChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  // Step 4: Register new user (save to localStorage)
  const registerUser = () => {
    if (registerData.username && registerData.password) {
      localStorage.setItem(registerData.username, JSON.stringify(registerData));
      alert("Registration successful!");
    } else {
      alert("Please fill out both fields.");
    }
  };

  // Step 5: Login user (verify from localStorage)
  const loginUser = () => {
    const storedUserData = localStorage.getItem(loginData.username);
    if (storedUserData) {
      const user = JSON.parse(storedUserData);
      if (user.password === loginData.password) {
        setLoggedInUser(user.username);
        alert("Login successful!");
      } else {
        alert("Incorrect password.");
      }
    } else {
      alert("User not found.");
    }
  };

  // Step 6: Logout user
  const logoutUser = () => {
    setLoggedInUser(null);
  };

  return (
    <div className="App">
      <h1>Simple Register and Login Example</h1>

      {/* If the user is logged in, show a welcome message */}
      {loggedInUser ? (
        <div>
          <h2>Welcome, {loggedInUser}!</h2>
          <button onClick={logoutUser}>Logout</button>
        </div>
      ) : (
        <div>
          {/* Registration Form */}
          <h2>Register</h2>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={registerData.username}
            onChange={handleRegisterChange}
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={registerData.password}
            onChange={handleRegisterChange}
          />
          <button onClick={registerUser}>Register</button>

          {/* Login Form */}
          <h2>Login</h2>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={loginData.username}
            onChange={handleLoginChange}
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={loginData.password}
            onChange={handleLoginChange}
          />
          <button onClick={loginUser}>Login</button>
        </div>
      )}
    </div>
  );
}

export default App;
