import React, { useState } from "react";

const CouponInput = () => {
  const [couponCode, setCouponCode] = useState("");
  const [isCouponValid, setIsCouponValid] = useState(false);

  // Example list of valid coupons
  const validCoupons = ["DISCOUNT10", "FREESHIP", "WELCOME20"];

  const handleInputChange = (e) => {
    const code = e.target.value.toUpperCase();
    setCouponCode(code);
    setIsCouponValid(validCoupons.includes(code));
  };

  const applyCoupon = () => {
    if (isCouponValid) {
      alert(`Coupon "${couponCode}" applied successfully!`);
    }
  };

  return (
    <div style={{ position: "relative", width: "100%", maxWidth: "400px" }}>
      <input
        type="text"
        value={couponCode}
        onChange={handleInputChange}
        placeholder="Enter coupon code"
        style={{
          width: "100%",
          padding: "8px 80px 8px 8px", // space for the button inside the input
          fontSize: "16px",
          borderRadius: "4px",
          border: "1px solid #ccc",
        }}
      />
      <button
        onClick={applyCoupon}
        disabled={!isCouponValid}
        style={{
          position: "absolute",
          right: "8px",
          top: "50%",
          transform: "translateY(-50%)",
          padding: "8px 12px",
          fontSize: "14px",
          cursor: isCouponValid ? "pointer" : "not-allowed",
          backgroundColor: isCouponValid ? "#4CAF50" : "#ccc",
          color: "white",
          border: "none",
          borderRadius: "4px",
        }}
      >
        Apply
      </button>
    </div>
  );
};

export default CouponInput;
